function init(conf) { 
  var html = '',
      dom = null,
      that = this,
      libraries = [],
      library = '',
      systems = [],
      system = '',
      sys = '',
      systemIds = '',
      length = 0,
      i = 0,
      list = '',
      platform = '',
      platformLibraries = [],
      systemClean = '';

  $('#designer-dialog').empty();

  libraries = this.require('db').collections().JSON.find({
    'platform': 'all'
  });

  // get platformspecific items
  switch(true) {
    case this.require('designer').isElectron() === true:
      platform = 'electron';
      break;
    case this.require('designer').isCordova() === true:
      platform = 'cordova';
      break;
    default:
      platform = 'web';
      break;
  }

  platformLibraries = this.require('db').collections().JSON.find({
    'platform': platform
  });

  libraries = libraries.concat(platformLibraries);

  // sort JSON
  libraries.sort(function (itemA, itemB) {
    if (itemA._id > itemB._id) {
      return 1;
    }
    if (itemA._id < itemB._id) {
      return -1;
    }
    return 0;
  });

  length = libraries.length;
  for (i = 0; i < length; i++) {
    library = this.require(libraries[i]._id);

    list = list + '<a class="list-group-item" id="designer-dialog-import-file-modal-library-' + library.id() + '">' +
        '<h4 class="list-group-item-heading">' + library.source().description.substr(0, 40).split('\n')[0].split('.')[0] + '</h4>' +
        '<p class="list-group-item-text">v' + library.source().version + '</p>' +
        '</a>';
  }

  systems = this.require('storage').get('system-designer-systems');

  if (systems) {
    systemIds = systems.systems;
  }
  length = systemIds.length;

  for (i = 0; i < length; i++) {
    system = this.require('storage').get(systemIds[i]);

    if (this.require('factory').system() && this.require('factory').system().id() === system._id) {
    } else {
        sys = sys + '<a class="list-group-item" id="designer-dialog-import-file-modal-systems-' + system._id + '">' +
            '<h4 class="list-group-item-heading">' + system.name + '</h4>' +
            '<p class="list-group-item-text">v' + system.version + '</p>' +
            '</a>';
    }
  }

  html = this.require('dialog-modal-import-file.html');
  document.querySelector('#designer-dialog').insertAdjacentHTML('afterbegin',
    html.source()
        .replace(/{{title}}/gi, this.title())
        .replace(/{{library}}/gi, list)
        .replace(/{{systems}}/gi, sys)
  );

  // init
  if (sys === '') {
    $('#designer-dialog-import-modal-from-systems-input').hide();
  }
  if (!this.require('factory').system()) {
    $('#designer-dialog-import-file-modal-merge').hide();
  }

  // systems events  
  var callbackSystemEvent = function cbSystemEvent(event) {
    var id = '',
        systems = null,
        length = 0,
        i = 0;

    if ($(this).hasClass('active')) {
      $(this).removeClass('active');
      that.data(null);
    } else {
      id = this.getAttribute('id').replace('designer-dialog-import-file-modal-systems-', '');

      that.data(that.require('storage').get(id));

      // remove old active
      systems = document.getElementById('designer-dialog-import-file-modal-systems');

      length = systems.children.length;
      for (i = 0; i < length; i++) {
        $(systems.children[i]).removeClass('active');
      }

      // add current active
      $(this).addClass('active');
    }
  };

  length = systemIds.length;
  for (i = 0; i < length; i++) {
    system = that.require('storage').get(systems.systems[i]);
    if (this.require('factory').system() && this.require('factory').system().id() === system._id) {
    } else {
      dom = document.getElementById('designer-dialog-import-file-modal-systems-' + system._id);
      dom.addEventListener('click', callbackSystemEvent);
    }
  }

  // library events  
  var callbackEvent = function cbEvent(event) {
    var id = '',
        libraries = null,
        length = 0,
        i = 0,
        system = {};

    if ($(this).hasClass('active')) {
      $(this).removeClass('active');
      that.data(null);
    } else {
      id = this.getAttribute('id').replace('designer-dialog-import-file-modal-library-', '');

      // case of extensions
      system = that.require(id).source();
      if (system.name.indexOf('extension-') !== -1) {
        system.name = 'extension-' + Math.floor((1 + Math.random()) * 0x10000).toString(16);
        system._id = that.require('factory').generateId();

        // to avoid an issue when removing extensions
        systemClean = JSON.stringify(system);

        systemClean = systemClean.replace(/d1bdde1f59f1befe/g, that.require('factory').generateId());
        systemClean = systemClean.replace(/n1467b1097a16481/g, that.require('factory').generateId());
        systemClean = systemClean.replace(/t1fb0b150c810528/g, that.require('factory').generateId());

        system = JSON.parse(systemClean);
      }

      that.data(system);

      // remove old active
      libraries = document.getElementById('designer-dialog-import-file-modal-library');

      length = libraries.children.length;
      for (i = 0; i < length; i++) {
        $(libraries.children[i]).removeClass('active');
      }

      // add current active
      $(this).addClass('active');
    }
  };

  length = libraries.length;
  for (i = 0; i < length; i++) {
    library = this.require(libraries[i]._id);
    dom = document.getElementById('designer-dialog-import-file-modal-library-' + library.id());

    dom.addEventListener('click', callbackEvent);
  }

  dom = document.getElementById('designer-dialog-import-modal-from-file');
  dom.addEventListener('click', function click(event) {
    this.data(null);
    $('#designer-dialog-import-modal-from-file-form').show();
    $('#designer-dialog-import-modal-from-systems-form').hide();
    $('#designer-dialog-import-modal-from-library-form').hide();
    $('#designer-dialog-import-file-modal-import').show();
    $('#designer-dialog-import-file-modal-merge').removeClass('btn-primary');
    $('#designer-dialog-import-file-modal-merge').addClass('btn-default');
  }.bind(this));

  dom = document.getElementById('designer-dialog-import-modal-from-library');
  dom.addEventListener('click', function click(event) {
    this.data(null);
    $('#designer-dialog-import-modal-from-library-form').show();
    $('#designer-dialog-import-modal-from-systems-form').hide();
    $('#designer-dialog-import-modal-from-file-form').hide();
    $('#designer-dialog-import-file-modal-import').show();
    $('#designer-dialog-import-file-modal-merge').removeClass('btn-primary');
    $('#designer-dialog-import-file-modal-merge').addClass('btn-default');
  }.bind(this));

  dom = document.getElementById('designer-dialog-import-modal-from-systems');
  dom.addEventListener('click', function click(event) {
    this.data(null);
    $('#designer-dialog-import-modal-from-library-form').hide();
    $('#designer-dialog-import-modal-from-systems-form').show();
    $('#designer-dialog-import-modal-from-file-form').hide();
    $('#designer-dialog-import-file-modal-import').hide();
    $('#designer-dialog-import-file-modal-merge').removeClass('btn-default');
    $('#designer-dialog-import-file-modal-merge').addClass('btn-primary');
  }.bind(this));

  dom = document.getElementById('designer-dialog-import-file-modal-cancel');
  dom.addEventListener('click', function click(event) {
    this.cancel();
  }.bind(this));

  dom = document.getElementById('designer-dialog-import-file-modal-merge');
  dom.addEventListener('click', function click(event) {
    if (this.data() && Object.keys(this.data()).length) {
      this.mergeSystem();
    }
  }.bind(this));

  dom = document.getElementById('designer-dialog-import-file-modal-import');
  dom.addEventListener('click', function click(event) {

    if (this.data() && Object.keys(this.data()).length) {
      this.importSystem();
    }
  }.bind(this));

  dom = document.getElementById('designer-dialog-import-file-modal-file');
  dom.addEventListener('change', function click(e) {
    e.stopPropagation();
    e.preventDefault();

    var files = e.target.files,
        reader = new FileReader(),
        json = '',
        that = this;

    reader.onload = function onload(event) {
      json = json + event.target.result;
    };
    reader.onloadend = function onloadend() {
      try {
        var sys = JSON.parse(json);

        if (
          typeof sys._id !== 'undefined' &&
          typeof sys.name !== 'undefined' &&
          typeof sys.description !== 'undefined' &&
          typeof sys.version !== 'undefined' &&
          typeof sys.schemas !== 'undefined'
          ) {
          that.data(sys);
        } else {
          that.data(null);
        } 
      } catch (e) {
        that.data(null);
      }
    };
    if (files[0]) {
      reader.readAsText(files[0], 'UTF-8');
    }
  }.bind(this));

  dom = document.getElementById('designer-dialog-import-file-modal');
  dom.addEventListener('keydown', function keydown(event) {

    if (event.keyCode === 13) {
      event.stopPropagation();
      event.preventDefault();
      if ($('#designer-dialog-import-modal-from-file').prop('checked') ||
          $('#designer-dialog-import-modal-from-library').prop('checked')
      ) {
        if (this.data() && Object.keys(this.data()).length) {
          this.importSystem();
        }
      }
      if ($('#designer-dialog-import-modal-from-systems').prop('checked')) {
        if (this.data() && Object.keys(this.data()).length) {
          this.mergeSystem();
        }
      }
      return false;
    }
  }.bind(this));
}

