function init(conf) { 
  var html = null,
      dom = null;

  $('#designer-dialog').empty();

  html = this.require('dialog-modal-drop-file.html');
  document.querySelector('#designer-dialog').insertAdjacentHTML('afterbegin',
    html.source()
        .replace(/{{title}}/gi, this.title())
        .replace(/{{message}}/gi, this.message())
  );

  //events
  dom = document.getElementById('designer-dialog-drop-file-modal-cancel');
  dom.addEventListener('click', function click(event) {
    this.cancel();
  }.bind(this));

  dom = document.getElementById('designer-dialog-drop-file-modal-merge');
  dom.addEventListener('click', function click(event) {
    this.require('factory').mergeSystem(this.data());

    this.require('designer').save();

    this.require('spaces').render();
    this.require('workspace').render();

    this.require('router').update();

    this.hide();
  }.bind(this));

  dom = document.getElementById('designer-dialog-drop-file-modal-import');
  dom.addEventListener('click', function click(event) {
    var System = this.require('System'),
      sys = null,
      state = this.require('state'),
      message = this.require('message');

    if (this.require('factory').system()) {
      this.require('factory').system().destroy();
    }
    sys = this.require('factory').create(this.data());

    // empty log
    state.logs().forEach(function (item) {
      this.logs().pop();
    }.bind(state));

    // hide log
    $('#designer-menu-item-logs-number').hide();
    document.querySelector('#designer-menu-item-logs-number').innerHTML = 0;

    this.require('designer').save();

    this.require('state').space(sys.name());
    this.require('spaces').render();
    this.require('workspace').render();

    this.require('router').update();

    this.hide();
    this.require('designer').save();

    message.success('Importation of the system is done.');	
  }.bind(this));

  dom = document.getElementById('designer-dialog-drop-file-modal');
  dom.addEventListener('keydown', function keydown(event) {
    if (event.keyCode === 13) {
      event.stopPropagation();
      event.preventDefault();
      document.getElementById('designer-dialog-drop-file-modal-import').click();
      return false;
    }
  }.bind(this));

  if (!this.require('factory').system()) {
    $('#designer-dialog-drop-file-modal-merge').hide();
  }
}

