function ok() { 
  var message = this.require('message');
  // code inspired from the blog post
  // https://thiscouldbebetter.wordpress.com/2012/12/18/loading-editing-and-saving-a-text-file-in-html5-using-javascrip/
  if (this.require('factory').system()) {
    var system = null,
      textToWrite = null,
      textFileAsBlob = null,
      fileNameToSaveAs = null,
      downloadLink = null;

    system = this.require('db').collections().System.find({
      '_id': this.require('factory').system().id()
    })[0];
    system = JSON.parse(JSON.stringify(system));

    switch (true) {
      case $('#designer-dialog-export-json').prop('checked'):
        textToWrite = this.require('export').toJSON($('#designer-dialog-export-isMaster').prop('checked'));
        fileNameToSaveAs = this.require('factory').system().name() + '.json';
        break;
      case $('#designer-dialog-export-javascript').prop('checked'):
        textToWrite = this.require('export').toJavaScript($('#designer-dialog-export-options-log-level-select').val());
        fileNameToSaveAs = this.require('factory').system().name() + '.js';
        break;
      case $('#designer-dialog-export-html').prop('checked'):
        textToWrite = this.require('export').toHTML($('#designer-dialog-export-options-log-level-select').val());
        fileNameToSaveAs = this.require('factory').system().name() + '.html';
        break;
      case $('#designer-dialog-export-node').prop('checked'):
        textToWrite = this.require('export').toNode($('#designer-dialog-export-options-log-level-select').val());
        fileNameToSaveAs = this.require('factory').system().name() + '.js';
        break;
      case $('#designer-dialog-export-graphviz').prop('checked'):
        textToWrite = this.require('export').toGraphviz($('#designer-dialog-export-options-diagram-type-select').val());
        fileNameToSaveAs = this.require('factory').system().name() + '.gv';
        break;
      default:
        break;
    }

    if (!this.require('designer').isElectron()) {

      textFileAsBlob = new Blob([textToWrite], {
        type: 'application/octet-stream'
      });

      if (navigator.msSaveBlob) {
        navigator.msSaveBlob(textFileAsBlob, fileNameToSaveAs);
      } else {
        downloadLink = document.createElement('a');
        document.body.appendChild(downloadLink);
        downloadLink.style.display = 'none';

        downloadLink.download = fileNameToSaveAs;
        downloadLink.href = window.URL.createObjectURL(textFileAsBlob);

        downloadLink.click();
        window.URL.revokeObjectURL(downloadLink.href);
      }

      this.hide();
    } else {
      this.hide();

      var { dialog } = require('@electron/remote');
      var fs = require('fs');

      dialog.showSaveDialog({
          'defaultPath': '~/' + fileNameToSaveAs
        }).then(function(ret) {
          var savePath = ret.filePath; 
          if (savePath) {
            fs.writeFile(savePath, textToWrite, function(err) {
              if (err) {
                message.danger('Exportation failed.');
              } else {
                message.success('Exportation is done.');
              }
            });
          }
      });
    }
  } else {
    message.warning('There is no system to export.');
  }
}

