function ok() { 
  var designer = this.require('designer'),
    that = this,
    name = null,
    system = {},
    ModelSystem = null,
    modelSystem = null;

  function canCreate(name) {
    var systems = that.require('storage').get('system-designer-systems'),
      systemIds = [],
      i = 0,
      result = true;

    if (systems) {
      systemIds = systems.systems;
    }
    length = systemIds.length;

    for (i = 0; i < length; i++) {
      system = that.require('storage').get(systemIds[i]);

      if (!system || system.name === name) {
        result = false;
        break;
      }
    }

    // internal default name
    if (name === 'default') {
      result = false;
    }

    return result;
  }

  // get value
  name = $('#designer-dialog-system-creation-name').val();

  // clean
  name = name.trim();
  name = name.replace(/\s|\t/gi, '-').replace(/[\!\"\#\$\%\&\'\(\)\*\+\,\.\/\:\;\<\=\>\?\@\[\\\]\^\`\{\|\}\~]/gi, '');

  if (name && canCreate(name)) {

    system = this.require('factory').create({'name': name}, true);

    ModelSystem = this.require('ModelSystem');
    modelSystem = new ModelSystem({
      'title': name
    });
    modelSystem.uuid = system._id;
    modelSystem.document(JSON.parse(JSON.stringify(system)));
    modelSystem.content(JSON.stringify(system));

    designer.save();

    this.hide();

    // hide log if any
    $('#designer-menu-item-logs-number').hide();
    document.querySelector('#designer-menu-item-logs-number').innerHTML = 0;

    this.require('message-help').remove();

    this.require('state').space(name);

    this.require('spaces').render();
    this.require('workspace').render();

    this.require('message').success('System created. You can now begin to create schemas.');
  }
}

