function ok() { 
  var that = this, 
    message = this.require('message'),
    behavior = {},
    ModelBehavior = null,
    modelBehavior = null,
    model = '',
    state = '',
    componentId = '';

  function _findSchemaId(compId) {
    var result = compId,
      modelName = '';

    for (modelName in that.require('factory').system().components()) {
      if (typeof that.require('factory').system().components()[modelName][compId] !== 'undefined') {
        result = modelName;
        break;
      }
    }
    return result;
  }

  // get value
  model = _findSchemaId(this.require('state').space());
  state = $('#designer-dialog-behavior-creation-state').val();
  componentId = this.require('state').space();

  behavior = this.require('factory').addBehavior(model, componentId, state);

  if (Object.keys(behavior).length) {
    ModelBehavior = this.require('ModelBehavior');

    modelBehavior = new ModelBehavior({
      'uuid': behavior._id
    });

    modelBehavior.title(state);
    modelBehavior.document(behavior);
    modelBehavior.content(JSON.parse(JSON.stringify(behavior.action)));

    this.hide();
    this.require('message-help').remove();

    modelBehavior.render();

    Prism.highlightAll();

    // little effect
    $('#designer-behavior-' + behavior._id).hide();
    $('#designer-behavior-' + behavior._id).fadeIn(1000);

    this.require('designer').save();

    this.require('channel').$designerCreateBehavior(behavior);
    message.success('Behavior created.');
  } else {
    this.hide();
    message.warning('Can not create two behaviors for a method.');
  }
}

