function importSystem() { 
  var sys = null,
    designer = this.require('designer'),
    message = this.require('message');

  if (Object.keys(this.data()).length) {
    if (this.require('factory').system()) {
      this.require('factory').system().destroy();
    }

    sys = this.require('factory').create(this.data());

    // empty log
    this.require('state').logs().forEach(function (item) {
      this.logs().pop();
    }.bind(this.require('state')));


    // hide log
    $('#designer-menu-item-logs-number').hide();
    document.querySelector('#designer-menu-item-logs-number').innerHTML = 0;

    designer.save();

    this.require('state').space(sys.name());

    this.require('spaces').render();
    this.require('workspace').render();

    this.require('router').update();

    this.hide();

    message.success('Importation of the system is done.');
  } else {
    message.danger('The system you try to import is invalid');
  }
}

