function init(conf) { 
  var html = null,
      dom = null;

  $('#designer-dialog').empty();

  html = this.require('dialog-modal-drop-diagram.html');
  document.querySelector('#designer-dialog').insertAdjacentHTML('afterbegin',
    html.source()
        .replace(/{{title}}/gi, this.title())
        .replace(/{{message}}/gi, this.message())
  );

  //events
  dom = document.getElementById('designer-dialog-drop-diagram-modal-cancel');
  dom.addEventListener('click', function click(event) {
    this.cancel();
  }.bind(this));

  dom = document.getElementById('designer-dialog-drop-diagram-modal-import');
  dom.addEventListener('click', function click(event) {
    this.require('viz').render(this.data());
    this.require('viz').data(this.data());
    this.hide();
  }.bind(this));

  dom = document.getElementById('designer-dialog-drop-diagram-modal');
  dom.addEventListener('keydown', function keydown(event) {
    if (event.keyCode === 13) {
      event.stopPropagation();
      event.preventDefault();
      document.getElementById('designer-dialog-drop-diagram-modal-import').click();
      return false;
    }
  }.bind(this));

  if (!this.require('factory').system()) {
    $('#designer-dialog-drop-diagram-modal-merge').hide();
  }
}

