function ok() { 
 if (this.deferredPrompt() && this.deferredPrompt().prompt) {
    this.deferredPrompt().prompt();
    this.deferredPrompt().userChoice.then(function()  {
      this.deferredPrompt(null);
    }.bind(this));
  } 

  var config = this.require('storage').get('system-designer-config');
  if (!config) {
