function render() {
  var length = 0,
    i = 0,
    item = null,
    domHeader = document.getElementById('designer-menubar-header'),
    domItems = document.getElementById('designer-menubar-items'),
    domAction = document.getElementById('designer-menubar-actions'),
    self = this,
    arr = window.location.href.split('#'),
    params = window.location.href.split('?messages='),
    messages = [],
    context = 'systems',
    space = '',
    designer = this.require('designer'),
    menuHeader = [],
    menuItems = [],
    menuActions = [],
    menuSearch = [];
    config = {},
    isModeAdvanced = false,
    platform = '',
    platformMenuItems = [];

  function _removeActive() {
    var length = 0,
      i = 0,
      item = null;

    length = domItems.children.length;
    for (i = 0; i < length; i++) {
      item = domItems.children[i];
      $(item).removeClass('active');
    }
  }

  // empty
  $('#designer-menubar-header').empty();
  $('#designer-menubar-items').empty();
  $('#designer-menubar-actions').empty();
  this.items([]);
  this.actions([]);

  // get mode    
  config = this.require('storage').get('system-designer-config');
  if (config && config.advancedMode) {
    isModeAdvanced = true;
  }

  // menu header
  menuHeader = this.require('db').collections().MenuHeader.find({
    'type': this.require('state').type()
  });
  this.header(this.require(menuHeader[0]._id));

  // menu items
  menuItems = this.require('db').collections().MenuItem.find({
    'type': this.require('state').type(),
    'platform': 'all'
  });

  // get platform specific items
  switch(true) {
    case this.require('designer').isElectron() === true:
      platform = 'electron';
      break;
    case this.require('designer').isCordova() === true:
      platform = 'cordova';
      break;
    default:
      platform = 'web';
      break;
  }

  platformMenuItems = this.require('db').collections().MenuItem.find({
    'type': this.require('state').type(),
    'platform': platform
  });

  menuItems = menuItems.concat(platformMenuItems);

  menuItems.sort(function (itemA, itemB) {
    if (itemA.position > itemB.position) {
      return 1;
    }
    if (itemA.position < itemB.position) {
      return -1;
    }
    return 0;
  });

  menuItems.forEach(function (menuItem) {
    var id = menuItem._id;
    var name = menuItem.name;

    if ((name === 'bundle' && self.require('state').type() === 'system') || (name === 'behavior' && self.require('state').type() === 'behavior')) {
      if (isModeAdvanced) {
        self.items().push(self.require(id));
      }
    } else {
      self.items().push(self.require(id));
    }
  });

  // menu actions
  menuActions = this.require('db').collections().MenuAction.find({
    'type': this.require('state').type()
  }).sort({
    'position': 1
  });

  menuActions.forEach(function (menuAction) {
    var id = menuAction._id;
    self.actions().push(self.require(id));
  });

  // header
  domHeader.insertAdjacentHTML('afterbegin', this.header().html().source());

  // items
  this.require('state').navigation().menu([]);
  this.items().forEach(function (item) {
    domItems.insertAdjacentHTML('beforeend', '<li>' + item.html().source() + '</>');
    this.require('state').navigation().menu().push('designer-menu-item-' + item.name());
  }.bind(this));

  // events
  var callback = function () {
    _removeActive();
    $(this).addClass('active');
  };

  length = domItems.children.length;
  for (i = 0; i < length; i++) {
    item = domItems.children[i];
    item.addEventListener('click', callback);
    item.addEventListener('click', function click() {
      this.click();
    }.bind(self.items(i)));
  }

  // actions
  this.actions().forEach(function (action) {
    domAction.insertAdjacentHTML('beforeend', '<li>' + action.html().source() + '</>');
  });

  length = domAction.children.length;
  for (i = 0; i < length; i++) {
    item = domAction.children[i];
    item.addEventListener('click', function click() {
      this.click();
    }.bind(self.actions(i)));
  }

  if (this.require('state').type() === 'designer') {
    // focus on first element
    // or restore the context
    if (arr.length > 2 && arr[2].length !== 0) {
      context = arr[2];
      context = context.split('?')[0];
    }
    if (arr.length > 3) {
      space = arr[3];
      space = space.split('?')[0];
    }
    if (arr.length > 4) {
      this.require('state').component(arr[4].split('?')[0]);
    }

    length = this.items().length;
    for (i = 0; i < length; i++) {
      if (this.items(i).name() === context) {
        item = domItems.children[i];
        $(item).addClass('active');
      }
    }
    if (space) {
      this.require('state').space(decodeURIComponent(space));
    }
    this.require('state').menu(context);

    this.require('router').update();

    // run messages
    if (params[1]) {
      messages = JSON.parse(decodeURIComponent(params[1]));
      if (runtime.require('factory').system() && runtime.require('factory').system().name() !== 'default') {
        this.require('state').messages(messages);
      }
    }
  } else {
    // focus on first element
    if (length > 0) {
      this.require('state').menu(this.items(0).name());
      item = domItems.children[0];
      $(item).addClass('active');
    }
  }

  $(function () {
    $('[data-toggle="tooltip"]').tooltip({ 'container': 'body', delay: { 'show': 1500, 'hide': 100 }, trigger: 'hover' });
  });
}
