function danger(message) {
  var id = requestAnimationFrame(function () {
    var html = this.require('message-alert-danger.html').source(),
        id = this.require('factory').generateId(),
        domId = 'designer-message-danger-' + id;

    this.clean();    

    message = message.replace('[object Object]', 'message can not be displayed because it is not a string.');

    document.querySelector('#designer-message').insertAdjacentHTML('afterbegin', 
      html.replace(/{{message}}/gi, message)
          .replace(/{id}/gi, id)
      );
    $('#' + domId).fadeIn(500);

    setTimeout(function () {
      $('#' + domId).fadeOut(1000, function() {
        $(this).remove();
      });
    }, 4000);
  }.bind(this));

  setTimeout(function () {
    cancelAnimationFrame(id);
  }, 50);
}

