function render() {
  var template = '';
  var message = '';
  var dom = null;
  var menu = this.require('state').menu();
  var system = this.require('factory').system();
  var hasSchema = false;

  function hasSystem() {
    var result = true;

    if (typeof system == 'undefined') {
      result = false;
    }
    if (typeof system !== 'undefined' && system.name() === 'default') {
      result = false;
    }

    return result;
  }

  if (hasSystem()) {
    hasSchema = Object.keys(system.schemas()).length !== 0;
  }

  template = this.require('message-help.html');

  switch (true) {
    case menu === 'systems':
      message = "To create a system, click on the '+' button or click here.";
      break;
    case menu === 'schemas' && hasSystem():
      message = "To create a schema, click on the '+' button or click here.";
      break;
    case menu === 'schemas' && !hasSystem():
      message = "To create a system, click here.";
      break;
    case menu === 'models' && !hasSystem():
      message = "To create a system, click here.";
      break;  
    case menu === 'models' && hasSystem() && !hasSchema:
      message = "To create a model, you need to create first a schema. Click here to create a schema.";
      break;
    case menu === 'types' && hasSystem():
      message = "To create a type, click on the '+' button or click here.";
      break;
    case menu === 'types' && !hasSystem():
      message = "To create a system, click here.";
      break;
    case menu === 'behaviors' && hasSystem():
      message = "To create a behavior, click on the '+' button or click here.";
      break;  
    case menu === 'behaviors' && !hasSystem():
      message = "To create a system, click here.";
      break;  
    case menu === 'components' && hasSystem() && hasSchema:
      message = "To create a component, click on the '+' button or click here.";
      break;
    case menu === 'components' && hasSystem() && !hasSchema:
      message = "To create a component, you need to create first a schema. Click here to create a schema.";
      break;
    case menu === 'components' && !hasSystem():
      message = "To create a system, click here.";
      break;
    case menu === 'logs' && !hasSystem():
      message = "To create a system, click here.";
      break;
    case menu === 'class' && !hasSystem():
      message = "To view a diagram, create first a system or drag and drop a Graphviz file here.";
      break;
    case menu === 'component' && !hasSystem():
      message = "To view a diagram, create first a system or drag and drop a Graphviz file here.";
      break;
    case menu === 'graph' && !hasSystem():
      message = "To view a diagram, create first a system or drag and drop a Graphviz file here.";
      break;
    default:
      break;
  }

  if (menu === 'class' || menu === 'component' || menu === 'graph') {
    document.querySelector('#designer-diagram').insertAdjacentHTML(
      'afterbegin',
      template.source().replace(/{{message}}/gi, message));
  } else {
    document.querySelector('#designer-workspace').insertAdjacentHTML(
      'afterbegin',
      template.source().replace(/{{message}}/gi, message));
  }

  // events
  dom = document.getElementById('panel-message-help');
  if (dom && menu !== 'class' && menu !== 'component' && menu !== 'graph') {
    dom.addEventListener('click', function click(event) {
      var menu = this.require('state').menu();
      var system = this.require('factory').system();
      var hasSchema = false;
      var Dialog = null;

      function hasSystem() {
        var result = true;

        if (typeof system == 'undefined') {
          result = false;
        }
        if (typeof system !== 'undefined' && system.name() === 'default') {
          result = false;
        }

        return result;
      }

      if (hasSystem()) {
        hasSchema = Object.keys(system.schemas()).length !== 0;
      }

      if (!hasSystem()) {
        Dialog = this.require('DialogSystemCreation');
        dialog = new Dialog({
          'title': 'Create a new system',
        });
        dialog.show();
      } else {
        switch (true) {
          case menu === 'models' && !hasSchema:
          case menu === 'components' && !hasSchema:
            Dialog = this.require('DialogSchemaCreation');
            dialog = new Dialog({
              'title': 'Create a new schema',
            });
            dialog.show();
            break;
          default:
            this.require('workspace').create();
            break;
        }
      }
    }.bind(this));
  }
}
